;; @@
(gg4clj/view [[:<- :g (->df data)]
              (gg4clj/r+
                [:ggplot :g [:aes {:x :he, :y :le, :color :country}]]
                [:geom_line {:size 0.5}]
                [:scale_colour_discrete {:guide "none"}]
                [:geom_dl [:aes {:label :country}] {:method [:list "last.points" {:cex 0.5 :rot 15 :hjust '-.1}]}]
                [:scale_x_continuous {:limits [:c 0 3000]}]
                [:theme {:axis.ticks [:element_blank]
                         :text [:element_text {:size 9}]}]
                [:labs {:title "Life expectancy vs. health expenditure, 1995 to 2014"
                        :subtitle "ASEAN"
                        :x "Health Expenditure in Million US$"
                        :y "Life Expectancy in Age"}])])
